axiom df-nmfn(setvar s0, setvar s1, setvar s2) {
  ⊢ normfn =(s0 ∈(ℂ[↦]~H)↦ sup({ s1 | ∃(s2 ∈ ~H,((normh ` s2)[≤]1 ∧ s1 =(abs `(s0 ` s2))))}, ℝ*, <))
  ⊣ d(s0, s1)
  ⊣ d(s0, s2)
  ⊣ d(s1, s2)
}

