axiom df-lnfn(setvar s0, setvar s1, setvar s2, setvar s3) {
  ⊢ LinFn = { s0 ∈(ℂ[↦]~H)| ∀(s1 ∈ ℂ, ∀(s2 ∈ ~H, ∀(s3 ∈ ~H,(s0 `((s1[.h]s2)[+h]s3))=((s1[×](s0 ` s2))[+](s0 ` s3)))))}
  ⊣ d(s0, s1)
  ⊣ d(s0, s2)
  ⊣ d(s0, s3)
  ⊣ d(s1, s2)
  ⊣ d(s1, s3)
  ⊣ d(s2, s3)
}
