blocks.origin:
axiom df-bra(setvar s0, setvar s1) {
  |- wceq(cbr, cmpt(s0, chba, cmpt(s1, chba, co(cv(s1), csp, cv(s0)))))
  -| diffss(s0, s1)
}

blocks.pretty:
axiom df-bra(setvar s0, setvar s1) {
  ⊢ bra =(s0 ∈ ~H ↦(s1 ∈ ~H ↦(s1[.ih]s0)))
  ⊣ d(s0, s1)
}

blocks.origin:
axiom df-kb(setvar s0, setvar s1, setvar s2) {
  |- wceq(ck, cmpo(s0, s1, chba, chba, cmpt(s2, chba, co(co(cv(s2), csp, cv(s1)), csm, cv(s0)))))
  -| diffss(s0, s1)
  -| diffss(s0, s2)
  -| diffss(s1, s2)
}

blocks.pretty:
axiom df-kb(setvar s0, setvar s1, setvar s2) {
  ⊢ ketbra =(s0 ∈ ~H, s1 ∈ ~H ↦(s2 ∈ ~H ↦((s2[.ih]s1)[.h]s0)))
  ⊣ d(s0, s1)
  ⊣ d(s0, s2)
  ⊣ d(s1, s2)
}
