axiom df-eigvec(setvar s0, setvar s1, setvar s2) {
  |- wceq(cei, cmpt(s0, co(chba, cmap, chba), crab(s1, cdif(chba, c0h), wrex(s2, cc, wceq(cfv(cv(s0), cv(s1)), co(cv(s2), csm, cv(s1)))))))
  -| diffss(s0, s1)
  -| diffss(s0, s2)
  -| diffss(s1, s2)
}

blocks.pretty:
axiom df-eigvec(setvar s0, setvar s1, setvar s2) {
  ⊢ eigvec =(s0 ∈(~H[↦]~H)↦ { s1 ∈(~H \ 0H)| ∃(s2 ∈ ℂ,(s0 ` s1)=(s2[.h]s1))})
  ⊣ d(s0, s1)
  ⊣ d(s0, s2)
  ⊣ d(s1, s2)
}

blocks.origin:
axiom df-eigval(setvar s0, setvar s1) {
  |- wceq(cel, cmpt(s0, co(chba, cmap, chba), cmpt(s1, cfv(cei, cv(s0)), co(co(cfv(cv(s0), cv(s1)), csp, cv(s1)), cdiv, co(cfv(cno, cv(s1)), cexp, nat2)))))
  -| diffss(s0, s1)
}

blocks.pretty:
axiom df-eigval(setvar s0, setvar s1) {
  ⊢ eigval =(s0 ∈(~H[↦]~H)↦(s1 ∈(eigvec ` s0)↦(((s0 ` s1)[.ih]s1)[÷]((normh ` s1)[^]2))))
  ⊣ d(s0, s1)
}

