axiom df-spec(setvar s0, setvar s1) {
  |- wceq(cspc, cmpt(s0, co(chba, cmap, chba), crab(s1, cc, wn(wf1(co(cv(s0), chod, co(cv(s1), chot, cres(cid, chba))), chba, chba)))))
