blocks.pretty:
axiom df-spec(setvar s0, setvar s1) {
  ⊢ Lambda =(s0 ∈(~H[↦]~H)↦ { s1 ∈ ℂ | ¬((s0[-op](s1[.op](= |` ~H))): ~H ↣ ~H)})
  ⊣ d(s0, s1)
}
