thm riesz2(setvar s0, setvar s1, class c0) {
  ⊢ ((c0 ∈ LinFn ∧(normfn ` c0)∈ ℝ)→ ∃!(s0 ∈ ~H, ∀(s1 ∈ ~H,(c0 ` s1)=(s1[.ih]s0))))
  ⊣ d(s0, s1)
  ⊣ d(s0, c0)
  ⊣ d(s1, c0)
} = {
  sylbir((c0 ∈ LinFn ∧(normfn ` c0)∈ ℝ), ∃!(s0 ∈ ~H, ∀(s1 ∈ ~H,(c0 ` s1)=(s1[.ih]s0))), c0 ∈(LinFn ⋂ ContFn))
  bitri(c0 ∈(LinFn ⋂ ContFn), (c0 ∈ LinFn ∧(normfn ` c0)∈ ℝ), (c0 ∈ LinFn ∧ c0 ∈ ContFn))
  elin(c0, clf, ccnfn)
  pm5.32i(c0 ∈ LinFn, c0 ∈ ContFn, (normfn ` c0)∈ ℝ)
  lnfncnbd(c0)
  riesz4(s0, s1, c0)
}
