thm eqri(setvar s0, class c0, class c1) {
  ⊢ c0 = c1
  ⊣ F/(s0, c0)
  ⊣ F/(s0, c1)
  ⊣ (s0 ∈ c0 ↔ s0 ∈ c1)
} = {
  mptru(c0 = c1)
  eqrd(s0, c0, c1, wtru)
  nftru(s0)
  a1i(wtru, (s0 ∈ c0 ↔ s0 ∈ c1))
}
