  simplbi(s0 ∈ { s0 ∈ c0 | w0 }, s0 ∈ c0, w0)
  rabid(s0, c0, w0)
  elex(c0, c1)
  ssexg({ s0 ∈ c0 | w0 }, c0, universe)
