thm uniin1(setvar s0, class c0, class c1) {
  |- wceq(ciun(s0, c0, cin(cv(s0), c1)), cin(cuni(c0), c1))
  -| diffsc(s0, c0)
  -| diffsc(s0, c1)
} = {
  eqtr4i(ciun(s0, c0, cin(cv(s0), c1)), cin(cuni(c0), c1), cin(ciun(s0, c0, cv(s0)), c1))
  iunin1(s0, c0, cv(s0), c1)
  ineq1i(cuni(c0), c1, ciun(s0, c0, cv(s0)))
  uniiun(s0, c0)
}

