thm uniin1(setvar s0, class c0, class c1) {
  ⊢ ⋃(s0 ∈ c0,(s0 ⋂ c1))=(⋃ c0 ⋂ c1)
  ⊣ d(s0, c0)
  ⊣ d(s0, c1)
} = {
  eqtr4i(⋃(s0 ∈ c0,(s0 ⋂ c1)), (⋃ c0 ⋂ c1), (⋃(s0 ∈ c0, s0)⋂ c1))
  iunin1(s0, c0, s0, c1)
  ineq1i(⋃ c0, c1, ⋃(s0 ∈ c0, s0))
  uniiun(s0, c0)
}

