thm disjeq1f(setvar s0, class c0, class c1, class c2) {
  ⊢ (c0 = c1 →(⊔(s0 ∈ c0, c2)↔ ⊔(s0 ∈ c1, c2)))
  ⊣ F/(s0, c0)
  ⊣ F/(s0, c1)
} = {
  impbid(c0 = c1, ⊔(s0 ∈ c0, c2), ⊔(s0 ∈ c1, c2))
  syl(c0 = c1, (⊔(s0 ∈ c0, c2)→ ⊔(s0 ∈ c1, c2)), c1 ⊆ c0)
  eqimss2(c0, c1)
  disjss1f(s0, c1, c0, c2)
  syl(c0 = c1, (⊔(s0 ∈ c1, c2)→ ⊔(s0 ∈ c0, c2)), c0 ⊆ c1)
  eqimss(c0, c1)
  disjss1f(s0, c0, c1, c2)
}
