thm drgextvsca(class c0, class c1, class c2, wff w0) {
  ⊢ (w0 →(× ` c0)=(× ` c1))
  ⊣ c1 =((subringAlg ` c0)` c2)
  ⊣ (w0 → c0 ∈ DivRing)
  ⊣ (w0 → c2 ∈(SubRing ` c0))
} = {
  a1ii((w0 →(× ` c0)=(× ` c1)), (w0 → c0 ∈ DivRing))
  sravsca(c0, c1, c2, w0)
  a1i(w0, c1 =((subringAlg ` c0)` c2))
  syl(w0, c2 ⊆(Base ` c0), c2 ∈(SubRing ` c0))
  subrgss(c2, c0, (Base ` c0))
  eqid((Base ` c0))
}
