  -| wi(w0, wcel(c2, ctop))
  -| wi(w0, wcel(c0, co(cdm(c1), cmbfm, cfv(csigagen, c2))))
} = {
  isanmbfm(c0, cdm(c1), cfv(csigagen, c2), w0)
  3syl(w0, wcel(cdm(c1), cuni(crn(csiga))), wcel(c1, cuni(crn(cmeas))), wcel(c1, cfv(cmeas, cdm(c1))))
  biimpi(wcel(c1, cuni(crn(cmeas))), wcel(c1, cfv(cmeas, cdm(c1))))
