  3syl(w0, dom(c1)∈ ⋃ ran(sigAlgebra), c1 ∈ ⋃ ran(measures), c1 ∈(measures ` dom(c1)))
  biimpi(c1 ∈ ⋃ ran(measures), c1 ∈(measures ` dom(c1)))
  measbasedom(c1)
  measbase(c1, dom(c1))
  sgsiga(c2, ctop, w0)
