thm coinflipuniv(class c0, class c1, class c2, class c3) {
  ⊢ ⋃ dom(c0)= { c1, c2 }
  ⊣ c1 ∈ 𝕌
  ⊣ c2 ∈ 𝕌
  ⊣ c1 ≠ c2
  ⊣ c0 =((# |` P({ c1, c2 }))[oFC(÷)]2)
  ⊣ c3 = { < c1, 1 >, < c2, 0 > }
} = {
  eqtri(⋃ dom(c0), { c1, c2 }, ⋃ P({ c1, c2 }))
  unieqi(dom(c0), P({ c1, c2 }))
  coinflipspace(c0, c1, c2, c3)
  unipw({ c1, c2 })
}
